import React from 'react'
import Card from 'react-bootstrap/Card';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import {useState} from 'react';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Popover from 'react-bootstrap/Popover';

function Media(props) {
console.log(props);
const navigate=useNavigate();
const [show, setShow] = useState(false);
const [coupon,setCoupon]=useState(false)

const handleClose = () => setShow(false);
const handleShow = () => setShow(true);



  return (
    <>
    <div></div>
    <div style={{display:"flex", justifyContent:"left",alignContent:"center",flexWrap:"wrap",margin:"20px"}}>
 <Card style={{width:"500px",height:"350px",flexBasis:"50%"}}>
      <Card.Header style={{textAlign:"center",color:"red"}}>ITEM(S) ADDED</Card.Header>
      <Card.Body>
        {/* <Card.Title>Special title treatment</Card.Title>
        <Card.Text>
          With supporting text below as a natural lead-in to additional content.
        </Card.Text> */}

<Card>
      {/* <Card.Header>Featured</Card.Header> */}
      <Card.Body>
        <Card.Title>{props.data.symbol}{props.data.name}</Card.Title>
        <Card.Text>
         cost:₹{props.data.val}
        </Card.Text>
        <Card.Text>
          Quantity:{props.data.count}
        </Card.Text>
      </Card.Body>
    </Card>


    <Card style={{marginTop:"10px",}}>
     
     <Card.Body>
       <Card.Text style={{borderBottom:"1px dashed grey",paddingBottom:"10px",cursor:"pointer"}} onClick={()=>{navigate("/")}}><i class="fa fa-plus-circle" aria-hidden="true"></i>Add more items<span style={{paddingLeft:"620px"}}><i class="fa fa-arrow-right" aria-hidden="true"></i></span></Card.Text>
       <Card.Text onClick={handleShow} style={{cursor:"pointer"}}><i class="fa fa-pencil-square-o" aria-hidden="true"></i>Add Cooking instructions<span style={{paddingLeft:"550px"}}><i class="fa fa-arrow-right" aria-hidden="true"></i></span></Card.Text>
       <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title style={{color:"red"}}>Special cooking instructions </Modal.Title>
        </Modal.Header>
        <Modal.Body>
        {/* <input type='text' placeholder='Start typing...' /> */}
        <textarea placeholder="Start typing..." cols={60} rows={4}></textarea>

        <p style={{color:"red",marginTop:"20px"}}>The restaurant will try their best to follow your instructions.However,no cancellation or refund will be possible if your request is not met</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose} style={{width:"470px"}}>
            Add
          </Button>
          {/* <Button variant="primary">Understood</Button> */}
        </Modal.Footer>
      </Modal>

     </Card.Body>
   </Card>


  
      
      </Card.Body>
    </Card>


    <Card style={{width:"500px",height:"250px",marginLeft:"2px",flexBasis:"50%",marginTop:"30px"}}>
      <Card.Header style={{textAlign:"center",color:"red"}}>SAVINGS CORNER</Card.Header>
      <Card.Body>
        
<Card>
      {/* <Card.Header>Featured</Card.Header> */}
      <Card.Body >
        {/* <Card.Title>{props.data.symbol}{props.data.name}</Card.Title> */}
        <Card.Text>
        Save ₹120 more on this order
        </Card.Text>
