import React from 'react'
import Card from 'react-bootstrap/Card';
import { useLocation } from 'react-router-dom'

function Media(props) {
console.log(props)
  
  return (
    <>
    <div style={{display:"flex",justifyContent:"center",alignContent:"center"}}>
 <Card style={{width:"500px",height:"500px",}}>
      <Card.Header style={{textAlign:"center"}}>ITEM(S) ADDED</Card.Header>
      <Card.Body>
        {/* <Card.Title>Special title treatment</Card.Title>
        <Card.Text>
          With supporting text below as a natural lead-in to additional content.
        </Card.Text> */}

<Card>
      {/* <Card.Header>Featured</Card.Header> */}
      <Card.Body>
        <Card.Title>{props.data.symbol}{props.data.name}</Card.Title>
        <Card.Text>
         cost:â‚¹{props.data.val}
        </Card.Text>
        <Card.Text>
          Quantity:{props.data.count}
        </Card.Text>
      </Card.Body>
    </Card>


    <Card style={{ma}}>
     
     <Card.Body>
       <Card.Text>Add more items</Card.Text>
       <Card.Text>Add Cooking instructions</Card.Text>
     
     </Card.Body>
   </Card>


      
      </Card.Body>
    </Card>


  
      
     
    </div>
    </>
  )
}


function Cart() {
  const location=useLocation();   
  const val1=location.state.val;
  const name1=location.state.name
  const symbol1=location.state.symbol;
  const count1=location.state.count
  console.log(val1,name1);
  let a={val:val1,name:name1,symbol:symbol1,count:count1}

  const handlelogout=async()=>{
    const result=await(await fetch("http://localhost:9000/rest/logout",{
      method:"POST",
      credentials:"include"
    })).json()
    
    console.log(result);
    // .then(res=>console.log(res.json()))
    // .then(a=>console.log(a))
    localStorage.setItem("token","")
     }
  return (
   <>
   <div className="head container">

   
<img src='https://mir-s3-cdn-cf.behance.net/projects/404/2971a3169377049.Y3JvcCwxMDgwLDg0NCwwLDExNw.png' style={{width:"190px",height:"160px",marginTop:"-40px"}} />

 {/* {localStorage.getItem("token")&&<h4>Welcome {((JSON.parse(localStorage.getItem("token"))).email)} {console.log((JSON.parse(localStorage.getItem("token"))).email)} </h4>
} */}

<ul className="list"> 
{/* <li><a href="/cart"><i class="fa fa-shopping-cart" aria-hidden="true"></i></a></li> */}
   <li><a href="/" onClick={handlelogout} >Logout</a></li>
   
   </ul>
</div>




<div>
      {/* <Media loading /> */}
      <Media data={a} />
    </div>

   </>
  )
}

export default Cart
